mapper.update:
delete FROM t_user
where id in

    #{item}

mapper.select:
SELECT	tu.card, tu.`name`, tt.type_name, tu.phone, tu.create_time, tu.expiry, tu.state, tt.remark
FROM t_user tu
JOIN t_type tt ON tu.type_id = tt.id


        `name` LIKE CONCAT('%', CONCAT(#{userPageDTO.name}, '%'))



        AND
        type_name = #{userPageDTO.typeName}



        AND
        expiry = #{userPageDTO.expiry}



        AND
        state = #{userPageDTO.state}



        AND
        card = #{userPageDTO.card}



        AND
        tu.create_time >=#{userPageDTO.openTime}



        AND
        tu.create_time <=#{userPageDTO.stopTime}

SELECT MONTH(t_access.scan_code_time) 'month', SUM(t_user.type_id = 1 OR t_user.type_id = 2 OR t_user.type_id = 4) totalNumber, SUM(t_user.type_id = 1) teacherNumber, SUM(t_user.type_id = 2) studentNumber, SUM(t_user.type_id = 4) visitorNumber FROM t_access LEFT JOIN t_user ON t_access.user_id = t_user.id LEFT JOIN t_qr ON t_access.qr_id = t_qr.id
AND t_access.qr_id = #{monthDTO.gatewayId}
GROUP BY MONTH(t_access.scan_code_time)

SELECT t_type.type_name typeName, SUM(t_access.qr_id = 1) entrance1, SUM(t_access.qr_id = 2) export1, SUM(t_access.qr_id = 3) export2, SUM(t_access.qr_id = 5) entrance2, SUM(t_access.qr_id = 6) export3 FROM t_access LEFT JOIN t_user ON t_access.user_id = t_user.id LEFT JOIN t_qr ON t_access.qr_id = t_qr.id LEFT JOIN t_type ON t_type.id = t_user.type_id
AND t_access.scan_code_time >= #{typeSelectDTO.startTime}
AND t_access.scan_code_time <= #{typeSelectDTO.stopTime}
GROUP BY t_type.type_name
